mapper.select:

select bno,title,content,writer,regdate,updatedate,viewcnt,isanonymous,likecnt
from (
 select /*+INDEX_DESC(tbl_board pk_board)*/
  rownum rn,bno,title,content,writer,regdate,updatedate,viewcnt,isanonymous,likecnt
 from tbl_board
 where rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum}-1) * #{amount}


select bno,title,content,writer,regdate,updatedate,viewcnt,isanonymous,likecnt
from (
 select /*+INDEX_DESC(tbl_board pk_board)*/
  rownum rn,bno,title,content,writer,regdate,updatedate,viewcnt,isanonymous,likecnt
 from tbl_board
 where rownum <= #{cri.pageNum} * #{cri.amount}
 and userid=#{userid}
)
where rn > (#{cri.pageNum}-1) * #{cri.amount}


select bno,title,content,writer,regdate,updatedate,viewcnt,isanonymous,likecnt
from (
 select /*+INDEX_DESC(tbl_board pk_board)*/
  rownum rn,tbl_board.bno,title,content,writer,regdate,updatedate,viewcnt,tbl_board.userid,isanonymous,likecnt
 from tbl_board,tbl_like
 where rownum <= #{cri.pageNum} * #{cri.amount}
 and tbl_board.bno = tbl_like.bno and tbl_like.userid=#{userid}
)
where rn > (#{cri.pageNum}-1) * #{cri.amount}


select count(*)
from tbl_board,tbl_like
where tbl_board.bno = tbl_like.bno and tbl_like.userid = #{userid}
